import React from 'react';
import styled from 'styled-components';
import PText from './PText';
import FooterColumn from './FooterColumn';

const FooterStyles = styled.div`
  padding-top: 10rem;
  background-color: var(--black);
  .container {
    display: flex;
    gap: 3rem;
  }
  .footer__column {
    flex: 2;
  }
  .footer__column2,
  .footer__column3,
  .footer__column4 {
    flex: 1;
  }
  .footer__column__title {
    font-size: 3.5rem;
    margin-bottom: 1rem;
  }
`;
export default function Footer() {
  return (
    <FooterStyles>
      <div className="container">
        <div className="footer__column">
          <h1 className="footer__column__title">Michael Devlin</h1>
          <PText>
            A web developer and designer based in London with an eye for UX and
            accessible design.
          </PText>
        </div>
        <div className="footer__column2">
          <FooterColumn
            heading="Links"
            links={[
              {
                title: 'Home',
                path: '/',
                type: 'Link',
              },
              {
                title: 'About',
                path: '/about',
                type: 'Link',
              },
              {
                title: 'Projects',
                path: '/projects',
                type: 'Link',
              },
              {
                title: 'Contact',
                path: '/contact',
                type: 'Link',
              },
            ]}
          />
        </div>
        <div className="footer__column3">
          <FooterColumn
            heading="Contact Info"
            links={[
              {
                title: 'michaeldevfay@gmail.com',
                path: 'mailto:michaeldevfay@gmail.com',
              },
              {
                title: '+447942679140',
              },
              {
                title: 'London, UK',
              },
            ]}
          />
        </div>
        <div className="footer__column4">
          <FooterColumn 
          heading="social Links"
          links={[
            {
              title: 'LinkedIn',
              path: 'https://www.linkedin.com/in/michael-devlin-/',
            },
            {
              title: 'GitHub',
              path: 'https://github.com/michael-devlin',
            },
            {
              title: 'Instagram',
              path: 'http://instagram.com',
            },
          ]}
          />
        </div>
      </div>
    </FooterStyles>
  );
}
